css:
/* RETIRAR CADASTRO DE PJ */
// esconder pessoa juridica
#account-form-type-j, #account-form-type-j + label {
display: none !important;
} 

/* MEGAMENU LARGURA DA TELA */
.header__submenu {
  max-width: none;
  min-width: 100%;
  left: 0%;
  gap: var(--spacer-1);
  margin-top: var(--spacer-2);
  grid-template-columns: repeat(auto-fit, minmax(10%, 1fr)); 
}

/* CENTRALIZAR NOME DO PRODUTO E PREÇO */
.category-retail .product-card__name, .category-retail .product-card__prices, .collection-shelf .product-card__name, .collection-shelf .product-card__prices, .page–search .product-card__name, .page–search .product-card__prices {text-align: center;
}

/* FONTES DO PRODUTO */
.product__name {
 font-size: 20px !important;
 color: #2a336c !important;
}
.prices__value {
 font-size: 25x !important;
 color: #000000 !important;
}
.variations__grid-title.h5 {
 font-size: 15px !important;
 color: #2a336c !important;
}
#product-description {
font-size: 10px
color: #000000
}

/* REMOVER CAMPO APENAS X UNIDADES */
